mapper.select:
SELECT * FROM PRCS



		PRCS_CD=#{prcsCd} and PRCS_NM=#{prcsNm}


		PRCS_CD=#{prcsCd}


		PRCS_NM=#{prcsNm}



order by PRCS_CD asc

mapper.update:
		UPDATE	PRCS
		SET PRCS_FG = #{prcsFg}
			, PRCS_NM = #{prcsNm}
            , PRCS_DESCT = #{prcsDesct}
            , PRCS_UNIT = #{prcsUnit}
			, PROD_PD = #{prodPd}
		WHERE PRCS_CD = #{prcsCd}
