mapper.select:
select p.prcs_fg, p.prcs_nm, p.prcs_desct, p.prcs_unit, p.prod_pd, e.eqm_cd, e.eqm_fg, e.eqm_nm, e.eqm_mdl, e.eqm_spec, e.eqm_co, e.purc_dt, e.purc_amt, e.li_no, e.emp_id, e.energy, e.lf, e.temp, e.uph, trunc((1/((e.uph/60)/60))*1000,3) AS pTime, e.eqm_img, e.prcs_cd, e.eqm_yn from prcs p, eqm e where p.prcs_cd = e.prcs_cd
AND e.prcs_cd = #{prcsCd}
order by eqm_cd
