mapper.select:
SELECT B.indica_deta_no, B.indica_no, B.indica_dt, (SELECT co_cd FROM order_tb WHERE order_no = P.order_no) co_cd, B.prdt_cd, TO_CHAR((SELECT recv_dt FROM order_tb WHERE order_no = P.order_no), 'YYYY/MM/DD') recv_dt, (SELECT prdt_nm FROM prdt WHERE prdt_cd = B.prdt_cd) prdt_nm, P.order_no, TO_CHAR((SELECT paprd_dt FROM order_tb WHERE order_no = P.order_no), 'YYYY/MM/DD') paprd_dt, O.order_qty, P.plan_deta_no, P.plan_dt, P.plan_qty, B.indica_qty, B.prod_fg, TO_CHAR(B.wk_dt, 'YYYY/MM/DD') wk_dt, B.wk_ord, (SELECT min(uph) FROM EQM) as day_out_put, (B.indica_qty/10) as prod_day FROM indica_d B JOIN plan_d P ON B.plan_deta_no = P.plan_deta_no JOIN order_d O ON (P.order_no = O.order_no AND B.prdt_cd = O.prdt_cd) WHERE 1=1
AND B.indica_dt >= TO_DATE(#{startT}, 'YYYY/MM/DD')
AND B.indica_dt <= TO_DATE(#{endT}, 'YYYY/MM/DD')
AND (SELECT co_cd FROM order_tb WHERE order_no = P.order_no) = #{coCd}
AND B.prdt_cd = #{prdtCd}
AND B.indica_no = #{indicaNo} ORDER BY indica_no

mapper.update:
UPDATE order_d
SET indica_qty = #{indicaQty}
WHERE order_no = #{orderNo}
AND prdt_cd = #{prdtCd}

UPDATE rsc_stc
SET	rsc_stc = rsc_stc - #{rscQty} 
WHERE rsc_cd = #{rscCd}

UPDATE indica
SET indica_dt = TO_DATE(#{indicaDt}, 'YYYY/MM/DD'),
	indica_nm = #{indicaNm}
WHERE indica_no = #{indicaNo}

UPDATE indica_d
SET	indica_dt = TO_DATE(#{indicaDt}, 'YYYY/MM/DD'),
	prod_fg = #{prodFg}
	indica_qty = #{indicaQty},
	wk_ord = #{wkOrd},
	wk_dt = #{wkDt}
WHERE indica_deta_no = #{indicaDetaNo}
AND indica_no = #{indicaNo}

mapper.delete:
DELETE FROM indica_d
WHERE indica_deta_no = #{indicaDetaNo}
AND indica_no = #{indicaNo}
