mapper.select:
SELECT B.plan_deta_no, A.plan_no, TO_CHAR(B.plan_dt, 'YYYY/MM/DD') plan_dt, (SELECT co_nm FROM co WHERE co_cd = O.co_cd) co_nm, O.co_cd, B.prdt_cd, (SELECT prdt_nm FROM prdt WHERE prdt_cd = B.prdt_cd) prdt_nm, B.order_no, TO_CHAR(O.paprd_dt, 'YYYY/MM/DD') paprd_dt, (SELECT order_qty FROM order_d WHERE order_no = B.order_no AND prdt_cd =B.prdt_cd) order_qty, B.plan_qty, TO_CHAR(B.wk_dt, 'YYYY/MM/DD') wk_dt, B.wk_ord, A.plan_dt, A.plan_nm, '10' as day_output, (B.plan_qty/10) as prod_day FROM plan A, plan_d B, order_tb O WHERE A.plan_no = B.plan_no AND B.order_no = O.order_no
AND B.plan_dt >= TO_DATE(#{startT}, 'YYYY/MM/DD')
AND B.plan_dt <= TO_DATE(#{endT}, 'YYYY/MM/DD')
AND O.co_cd = #{coCd}
AND B.prdt_cd = #{prdtCd}
AND B.plan_no = #{planNo}
ORDER BY paprd_dt, order_qty

mapper.delete:
DELETE FROM plan_d
WHERE plan_deta_no = #{planDetaNo}
AND plan_no = #{planNo}

mapper.update:
UPDATE plan
SET plan_dt = TO_DATE(#{planDt}, 'YYYY/MM/DD'),
	plan_nm = #{planNm}
WHERE plan_no = #{planNo}

update.if:
UPDATE plan_D
SET	plan_dt = #{planDt},
	plan_qty = #{planQty},
	wk_ord = #{wkOrd},
	wk_dt = #{wkDt}
WHERE plan_deta_no = #{planDetaNo}
AND plan_no = #{planNo}
