mapper.select:
SELECT B.plan_deta_no, A.plan_no, TO_CHAR(B.plan_dt, 'YYYY/MM/DD') plan_dt, (SELECT co_nm FROM co WHERE co_cd = O.co_cd) co_nm, O.co_cd, B.prdt_cd, (SELECT prdt_nm FROM prdt WHERE prdt_cd = B.prdt_cd) prdt_nm, B.order_no, TO_CHAR(O.paprd_dt, 'YYYY/MM/DD') paprd_dt, (SELECT order_qty FROM order_d WHERE order_no = B.order_no AND prdt_cd =B.prdt_cd) order_qty, B.plan_qty, TO_CHAR(B.wk_dt, 'YYYY/MM/DD') wk_dt, B.wk_ord FROM plan A, plan_d B, order_tb O WHERE A.plan_no = B.plan_no AND B.order_no = O.order_no
AND B.plan_dt >= TO_DATE(#{planStartDt}, 'YYYY/MM/DD')
AND B.plan_dt <= TO_DATE(#{planEndDt}, 'YYYY/MM/DD')
AND O.co_cd = #{coCd}
AND B.prdt_cd = #{prdtCd}
AND plan_no = #{planNo}
ORDER BY paprd_dt, order_qty

SELECT * 
FROM co



			co_cd = #{coCd} and co_nm = #{coNm}


			co_cd = #{coCd}


			co_nm = #{coNm}

SELECT *
FROM prdt



				prdt_cd = #{prdtCd} and prdt_nm = #{prdtNm}


				prdt_cd = #{prdtCd}


				prdt_nm = #{prdtNm}



	ORDER BY prdt_cd asc
