mapper.update:
update rsc_stc
set rsc_stc = rsc_stc + #{rscQty}
where rsc_cd = #{rscCd}

mapper.select:
SELECT to_char(i.rsc_dt,'yyyy/MM/dd') "rsc_dt", r.rsc_nm, i.rsc_cd, i.rsc_lot, i.rsc_qty, i.rsc_slip_no, i.rsc_amt, c.co_nm FROM rsc_inout i JOIN rsc r ON i.rsc_cd = r.rsc_cd JOIN co c ON r.co_cd = c.co_cd WHERE 1 = 1
AND trunc(i.rsc_dt) >= #{ordrDtStt}
AND trunc(i.rsc_dt) <= #{ordrDtEnd}
AND c.co_cd = #{co}
AND i.rsc_cd = #{rsc}
ORDER BY ordr_dt
