mapper.select:
SELECT to_char(o.ordr_dt,'yyyy/MM/dd') "ordr_dt", r.rsc_nm, r.rsc_cd, o.ordr_qty, o.ordr_cd, c.co_nm, o.rsc_ist_qty, o.rsc_infer_qty, o.insp_cls, o.rsc_ist_qty - o.rsc_infer_qty "rsc_passed_qty" FROM rsc_or o JOIN rsc r ON o.rsc_cd = r.rsc_cd JOIN co c ON r.co_cd = c.co_cd WHERE 1 = 1
AND o.insp_cls = #{inspCls}
AND o.ordr_qty != o.rsc_infer_qty
AND o.insp_cls in ('rs001','rs002')
AND trunc(o.ordr_dt) >= #{ordrDtStt}
AND trunc(o.ordr_dt) <= #{ordrDtEnd}
AND c.co_cd = #{co}
AND o.rsc_cd = #{rsc}
ORDER BY ordr_dt
