mapper.select:
		select rr.*, o.co_cd, o.rsc_ist_qty, o.rsc_infer_qty, r.rsc_nm, r.co_cd
		from rsc_rt rr
		join rsc_or o
		on rr.ordr_cd = o.ordr_cd
        join rsc r
        on o.rsc_cd = r.rsc_cd
		WHERE
		    1 = 1

			AND trunc(o.ordr_dt) >= #{ordrDtStt}


			AND trunc(o.ordr_dt) <= #{ordrDtEnd}


			AND o.co_cd = #{co}


			AND r.rsc_cd = #{rsc}
